client/src/pages/pricing.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Check, Sparkles, Zap, Crown, Building2, Star } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { cn } from "@/lib/utils";
import { useState } from "react";
import { useLocation } from "wouter";

interface SubscriptionPlan {
  id: string;
  name: string;
  displayName: string;
  monthlyPrice: string;
  yearlyPrice: string;
  creditsPerMonth: number;
  features: string[];
  maxPlatforms: number;
  analyticsAccess: boolean;
  aiSuggestions: boolean;
  prioritySupport: boolean;
  teamMembers: number;
  videoGeneration: boolean;
  popular?: boolean;
}

// Credit cost breakdown
const CREDIT_COSTS = {
  textPost: 1,
  aiTextGeneration: 5,
  imageGeneration: 50,
  videoGeneration: 500,
};

export default function Pricing() {
  const { toast } = useToast();
  const [, navigate] = useLocation();
  const [billingCycle, setBillingCycle] = useState<'monthly' | 'yearly'>('monthly');
  const { data: user } = useQuery<{ subscriptionId?: string }>({ 
    queryKey: ["/api/auth/user"],
    retry: false,
    enabled: false // Don't automatically fetch user data
  });
  const { data: plans = [], isLoading } = useQuery<SubscriptionPlan[]>({
    queryKey: ["/api/subscription-plans"],
  });

  const handleSubscribe = (planId: string) => {
    // Navigate to Stripe payment page with plan details
    navigate(`/subscribe?plan=${planId}&cycle=${billingCycle}`);
  };

  const getPlanIcon = (planId: string) => {
    switch (planId) {
      case "free":
        return <Star className="w-5 h-5" />;
      case "starter":
        return <Sparkles className="w-5 h-5" />;
      case "professional":
        return <Zap className="w-5 h-5" />;
      case "enterprise":
        return <Crown className="w-5 h-5" />;
      default:
        return <Building2 className="w-5 h-5" />;
    }
  };

  const formatPrice = (price: string) => {
    return parseFloat(price).toFixed(0);
  };

  // Add popular flag to professional plan
  const enhancedPlans = plans.map(plan => ({
    ...plan,
    popular: plan.id === "professional",
  }));

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-12">
      {/* Header */}
      <div className="text-center mb-12">
        <h1 className="text-4xl font-bold mb-4">Choose Your Plan</h1>
        <p className="text-xl text-muted-foreground max-w-2xl mx-auto">
          Scale your social media presence with AI-powered content generation and management
        </p>

        {/* Billing Cycle Toggle */}
        <div className="mt-6 flex items-center justify-center gap-4">
          <Button
            variant={billingCycle === 'monthly' ? 'default' : 'outline'}
            onClick={() => setBillingCycle('monthly')}
            size="sm"
            data-testid="button-monthly"
          >
            Monthly
          </Button>
          <Button
            variant={billingCycle === 'yearly' ? 'default' : 'outline'}
            onClick={() => setBillingCycle('yearly')}
            size="sm"
            data-testid="button-yearly"
          >
            Yearly (Save 20%)
          </Button>
        </div>

        {/* Credit Calculator */}
        <div className="mt-8 p-6 bg-muted/50 rounded-lg max-w-3xl mx-auto">
          <h3 className="font-semibold mb-3">Credit Usage Guide</h3>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
            <div className="text-center">
              <div className="font-semibold text-2xl">{CREDIT_COSTS.textPost}</div>
              <div className="text-muted-foreground">Text Post</div>
            </div>
            <div className="text-center">
              <div className="font-semibold text-2xl">{CREDIT_COSTS.aiTextGeneration}</div>
              <div className="text-muted-foreground">AI Text</div>
            </div>
            <div className="text-center">
              <div className="font-semibold text-2xl">{CREDIT_COSTS.imageGeneration}</div>
              <div className="text-muted-foreground">Image</div>
            </div>
            <div className="text-center">
              <div className="font-semibold text-2xl">{CREDIT_COSTS.videoGeneration}</div>
              <div className="text-muted-foreground">Video (8s)</div>
            </div>
          </div>
        </div>
      </div>

      {/* Pricing Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 max-w-7xl mx-auto">
        {enhancedPlans.map((plan) => (
          <Card 
            key={plan.id}
            className={cn(
              "relative flex flex-col",
              plan.popular && "border-primary shadow-lg scale-105"
            )}
          >
            {plan.popular && (
              <Badge className="absolute -top-3 left-1/2 transform -translate-x-1/2">
                Most Popular
              </Badge>
            )}

            <CardHeader>
              <div className="flex items-center gap-2 mb-2">
                {getPlanIcon(plan.id)}
                <CardTitle>{plan.displayName}</CardTitle>
              </div>
              <div className="mt-4">
                <span className="text-3xl font-bold">
                  ${billingCycle === 'monthly' ? 
                    formatPrice(plan.monthlyPrice.toString()) : 
                    formatPrice(plan.yearlyPrice.toString())}
                </span>
                <span className="text-muted-foreground">
                  /{billingCycle === 'monthly' ? 'month' : 'year'}
                </span>
              </div>
              <CardDescription>
                {plan.creditsPerMonth.toLocaleString()} credits/month
                {billingCycle === 'yearly' && (
                  <Badge variant="secondary" className="ml-2">
                    Save 20%
                  </Badge>
                )}
              </CardDescription>
            </CardHeader>

            <CardContent className="flex-1">
              <ul className="space-y-2">
                {plan.features.map((feature, index) => (
                  <li key={index} className="flex items-start">
                    <Check className="w-4 h-4 text-green-500 mr-2 mt-0.5 flex-shrink-0" />
                    <span className="text-sm">{feature}</span>
                  </li>
                ))}
              </ul>

              {/* Usage Examples */}
              <div className="mt-6 p-3 bg-muted/50 rounded text-xs">
                <div className="font-semibold mb-1">Monthly capacity:</div>
                <div className="space-y-1 text-muted-foreground">
                  <div>â€¢ {Math.floor(plan.creditsPerMonth / CREDIT_COSTS.textPost)} text posts</div>
                  <div>â€¢ {Math.floor(plan.creditsPerMonth / CREDIT_COSTS.aiTextGeneration)} AI generations</div>
                  {plan.creditsPerMonth >= CREDIT_COSTS.imageGeneration && (
                    <div>â€¢ {Math.floor(plan.creditsPerMonth / CREDIT_COSTS.imageGeneration)} images</div>
                  )}
                  {plan.videoGeneration && (
                    <div>â€¢ {Math.floor(plan.creditsPerMonth / CREDIT_COSTS.videoGeneration)} videos</div>
                  )}
                </div>
              </div>
            </CardContent>

            <CardFooter>
              <Button 
                className="w-full" 
                variant={plan.popular ? "default" : "outline"}
                onClick={() => handleSubscribe(plan.id)}
                disabled={user?.subscriptionId === plan.id}
                data-testid={`button-subscribe-${plan.id}`}
              >
                {user?.subscriptionId === plan.id ? "Current Plan" : 
                 plan.id === "free" ? "Start 7-Day Free Trial" : "Subscribe Now"}
              </Button>
            </CardFooter>
          </Card>
        ))}
      </div>

      {/* Additional Information */}
      <div className="mt-16 max-w-4xl mx-auto">
        <Card>
          <CardHeader>
            <CardTitle>Frequently Asked Questions</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <h3 className="font-semibold mb-1">What are credits?</h3>
              <p className="text-sm text-muted-foreground">
                Credits are used for content generation and platform features. Different actions consume different amounts of credits based on computational cost.
              </p>
            </div>
            <div>
              <h3 className="font-semibold mb-1">Do unused credits roll over?</h3>
              <p className="text-sm text-muted-foreground">
                No, credits reset each month. We recommend choosing a plan that matches your typical monthly usage.
              </p>
            </div>
            <div>
              <h3 className="font-semibold mb-1">Can I change plans anytime?</h3>
              <p className="text-sm text-muted-foreground">
                Yes! You can upgrade or downgrade your plan at any time. Changes take effect immediately and are prorated.
              </p>
            </div>
            <div>
              <h3 className="font-semibold mb-1">What payment methods do you accept?</h3>
              <p className="text-sm text-muted-foreground">
                We accept all major credit cards and debit cards through our secure Stripe payment processor.
              </p>
            </div>
            <div>
              <h3 className="font-semibold mb-1">Is there a free trial?</h3>
              <p className="text-sm text-muted-foreground">
                Yes! The Free plan includes a 7-day trial with full access. You'll need to provide a valid credit card, but won't be charged during the trial period.
              </p>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Enterprise CTA */}
      <div className="mt-12 text-center p-8 bg-gradient-to-r from-primary/10 to-primary/5 rounded-lg">
        <h2 className="text-2xl font-bold mb-3">Need a Custom Solution?</h2>
        <p className="text-muted-foreground mb-6 max-w-2xl mx-auto">
          Get custom credit packages, dedicated support, and enterprise features tailored to your business needs.
        </p>
        <Button size="lg" variant="outline">
          Contact Sales
        </Button>
      </div>
    </div>
  );
}

client/src/pages/settings.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Switch } from "@/components/ui/switch";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { 
  User, 
  Building, 
  Palette, 
  Bell, 
  Shield, 
  Link as LinkIcon,
  CheckCircle,
  AlertCircle,
  Settings as SettingsIcon
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import type { User as UserType, Platform } from "@shared/schema";

export default function Settings() {
  const [activeTab, setActiveTab] = useState("profile");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: user } = useQuery<UserType>({
    queryKey: ["/api/user"],
  });

  const { data: platforms } = useQuery<Platform[]>({
    queryKey: ["/api/platforms"],
  });

  const [profileData, setProfileData] = useState({
    fullName: user ? `${user.firstName || ''} ${user.lastName || ''}`.trim() : "",
    businessName: user?.businessName || "",
    avatar: user?.profileImageUrl || "",
  });

  const [brandSettings, setBrandSettings] = useState({
    brandColors: {
      primary: "#3b82f6",
      secondary: "#64748b",
      accent: "#f59e0b",
    },
    toneOfVoice: "friendly",
    brandKeywords: "",
    logoUrl: "",
  });

  const [notificationSettings, setNotificationSettings] = useState({
    emailNotifications: true,
    pushNotifications: true,
    weeklyReports: true,
    approvalReminders: true,
    publishingAlerts: true,
  });

  const updateProfileMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest("PATCH", "/api/user", data);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Profile updated successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/user"] });
    },
  });

  const connectPlatformMutation = useMutation({
    mutationFn: async (platformName: string) => {
      return apiRequest("POST", "/api/platforms/connect", { platform: platformName });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Platform connected successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/platforms"] });
    },
  });

  const handleProfileUpdate = () => {
    updateProfileMutation.mutate(profileData);
  };

  const getPlatformIcon = (platform: string) => {
    const iconMap: { [key: string]: string } = {
      "Instagram": "fab fa-instagram text-pink-500",
      "Facebook": "fab fa-facebook text-blue-600",
      "X (Twitter)": "fab fa-twitter text-blue-400",
      "TikTok": "fab fa-tiktok text-gray-800",
      "LinkedIn": "fab fa-linkedin text-blue-700",
    };
    return iconMap[platform] || "fab fa-share text-gray-500";
  };

  const allPlatforms = [
    { name: "Instagram", description: "Connect your Instagram business account" },
    { name: "Facebook", description: "Connect your Facebook page" },
    { name: "X (Twitter)", description: "Connect your Twitter account" },
    { name: "TikTok", description: "Connect your TikTok business account" },
    { name: "LinkedIn", description: "Connect your LinkedIn company page" },
  ];

  return (
    <div className="p-6">
      <div className="max-w-4xl mx-auto">
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <SettingsIcon className="w-5 h-5" />
              <span>Settings</span>
            </CardTitle>
            <p className="text-sm text-muted-foreground">
              Configure your account and platform settings
            </p>
          </CardHeader>

          <CardContent>
            <Tabs value={activeTab} onValueChange={setActiveTab}>
              <TabsList className="grid w-full grid-cols-4">
                <TabsTrigger value="profile">Profile</TabsTrigger>
                <TabsTrigger value="platforms">Platforms</TabsTrigger>
                <TabsTrigger value="brand">Brand</TabsTrigger>
                <TabsTrigger value="notifications">Notifications</TabsTrigger>
              </TabsList>

              <div className="mt-6">
                {/* Profile Settings */}
                <TabsContent value="profile" className="space-y-6">
                  <div className="space-y-4">
                    <div className="flex items-center space-x-2">
                      <User className="w-5 h-5 text-muted-foreground" />
                      <h3 className="text-lg font-semibold">Personal Information</h3>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      <div className="space-y-2">
                        <Label htmlFor="fullName">Full Name</Label>
                        <Input
                          id="fullName"
                          value={profileData.fullName}
                          onChange={(e) => setProfileData({ ...profileData, fullName: e.target.value })}
                          placeholder="Enter your full name"
                        />
                      </div>
                      <div className="space-y-2">
                        <Label htmlFor="businessName">Business Name</Label>
                        <Input
                          id="businessName"
                          value={profileData.businessName}
                          onChange={(e) => setProfileData({ ...profileData, businessName: e.target.value })}
                          placeholder="Enter your business name"
                        />
                      </div>
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="avatar">Profile Picture URL</Label>
                      <Input
                        id="avatar"
                        value={profileData.avatar}
                        onChange={(e) => setProfileData({ ...profileData, avatar: e.target.value })}
                        placeholder="Enter profile picture URL"
                      />
                    </div>

                    <Button onClick={handleProfileUpdate} disabled={updateProfileMutation.isPending}>
                      {updateProfileMutation.isPending ? "Updating..." : "Update Profile"}
                    </Button>
                  </div>

                  <Separator />

                  <div className="space-y-4">
                    <div className="flex items-center space-x-2">
                      <Shield className="w-5 h-5 text-muted-foreground" />
                      <h3 className="text-lg font-semibold">Security</h3>
                    </div>

                    <div className="space-y-4">
                      <Button variant="outline">Change Password</Button>
                      <Button variant="outline">Two-Factor Authentication</Button>
                    </div>
                  </div>
                </TabsContent>

                {/* Platform Settings */}
                <TabsContent value="platforms" className="space-y-6">
                  <div className="flex items-center space-x-2">
                    <LinkIcon className="w-5 h-5 text-muted-foreground" />
                    <h3 className="text-lg font-semibold">Connected Platforms</h3>
                  </div>

                  <div className="space-y-4">
                    {allPlatforms.map((platform) => {
                      const connectedPlatform = platforms?.find(p => p.name === platform.name);
                      const isConnected = connectedPlatform?.isConnected;

                      return (
                        <div
                          key={platform.name}
                          className="flex items-center justify-between p-4 border border-border rounded-lg"
                        >
                          <div className="flex items-center space-x-3">
                            <i className={getPlatformIcon(platform.name)} />
                            <div>
                              <p className="font-medium text-foreground">{platform.name}</p>
                              <p className="text-sm text-muted-foreground">{platform.description}</p>
                            </div>
                          </div>
                          <div className="flex items-center space-x-3">
                            {isConnected ? (
                              <>
                                <Badge variant="outline" className="bg-green-50 text-green-700 border-green-200">
                                  <CheckCircle className="w-3 h-3 mr-1" />
                                  Connected
                                </Badge>
                                <Button variant="outline" size="sm">
                                  Disconnect
                                </Button>
                              </>
                            ) : (
                              <>
                                <Badge variant="outline" className="bg-gray-50 text-gray-700 border-gray-200">
                                  <AlertCircle className="w-3 h-3 mr-1" />
                                  Not Connected
                                </Badge>
                                <Button 
                                  size="sm"
                                  onClick={() => connectPlatformMutation.mutate(platform.name)}
                                  disabled={connectPlatformMutation.isPending}
                                >
                                  Connect
                                </Button>
                              </>
                            )}
                          </div>
                        </div>
                      );
                    })}
                  </div>
                </TabsContent>

                {/* Brand Settings */}
                <TabsContent value="brand" className="space-y-6">
                  <div className="flex items-center space-x-2">
                    <Palette className="w-5 h-5 text-muted-foreground" />
                    <h3 className="text-lg font-semibold">Brand Guidelines</h3>
                  </div>

                  <div className="space-y-6">
                    <div className="space-y-4">
                      <h4 className="font-medium">Brand Colors</h4>
                      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                        <div className="space-y-2">
                          <Label htmlFor="primaryColor">Primary Color</Label>
                          <div className="flex items-center space-x-2">
                            <Input
                              id="primaryColor"
                              type="color"
                              value={brandSettings.brandColors.primary}
                              onChange={(e) => setBrandSettings({
                                ...brandSettings,
                                brandColors: { ...brandSettings.brandColors, primary: e.target.value }
                              })}
                              className="w-16 h-10"
                            />
                            <Input
                              value={brandSettings.brandColors.primary}
                              onChange={(e) => setBrandSettings({
                                ...brandSettings,
                                brandColors: { ...brandSettings.brandColors, primary: e.target.value }
                              })}
                              placeholder="#3b82f6"
                            />
                          </div>
                        </div>
                        <div className="space-y-2">
                          <Label htmlFor="secondaryColor">Secondary Color</Label>
                          <div className="flex items-center space-x-2">
                            <Input
                              id="secondaryColor"
                              type="color"
                              value={brandSettings.brandColors.secondary}
                              onChange={(e) => setBrandSettings({
                                ...brandSettings,
                                brandColors: { ...brandSettings.brandColors, secondary: e.target.value }
                              })}
                              className="w-16 h-10"
                            />
                            <Input
                              value={brandSettings.brandColors.secondary}
                              onChange={(e) => setBrandSettings({
                                ...brandSettings,
                                brandColors: { ...brandSettings.brandColors, secondary: e.target.value }
                              })}
                              placeholder="#64748b"
                            />
                          </div>
                        </div>
                        <div className="space-y-2">
                          <Label htmlFor="accentColor">Accent Color</Label>
                          <div className="flex items-center space-x-2">
                            <Input
                              id="accentColor"
                              type="color"
                              value={brandSettings.brandColors.accent}
                              onChange={(e) => setBrandSettings({
                                ...brandSettings,
                                brandColors: { ...brandSettings.brandColors, accent: e.target.value }
                              })}
                              className="w-16 h-10"
                            />
                            <Input
                              value={brandSettings.brandColors.accent}
                              onChange={(e) => setBrandSettings({
                                ...brandSettings,
                                brandColors: { ...brandSettings.brandColors, accent: e.target.value }
                              })}
                              placeholder="#f59e0b"
                            />
                          </div>
                        </div>
                      </div>
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="logoUrl">Logo URL</Label>
                      <Input
                        id="logoUrl"
                        value={brandSettings.logoUrl}
                        onChange={(e) => setBrandSettings({ ...brandSettings, logoUrl: e.target.value })}
                        placeholder="Enter your logo URL"
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="brandKeywords">Brand Keywords</Label>
                      <Textarea
                        id="brandKeywords"
                        value={brandSettings.brandKeywords}
                        onChange={(e) => setBrandSettings({ ...brandSettings, brandKeywords: e.target.value })}
                        placeholder="Enter keywords that represent your brand (e.g., coffee, cozy, local, artisan)"
                        rows={3}
                      />
                    </div>

                    <Button>Save Brand Settings</Button>
                  </div>
                </TabsContent>

                {/* Notification Settings */}
                <TabsContent value="notifications" className="space-y-6">
                  <div className="flex items-center space-x-2">
                    <Bell className="w-5 h-5 text-muted-foreground" />
                    <h3 className="text-lg font-semibold">Notification Preferences</h3>
                  </div>

                  <div className="space-y-6">
                    <div className="space-y-4">
                      <div className="flex items-center justify-between">
                        <div>
                          <p className="font-medium">Email Notifications</p>
                          <p className="text-sm text-muted-foreground">Receive email updates about your account</p>
                        </div>
                        <Switch
                          checked={notificationSettings.emailNotifications}
                          onCheckedChange={(checked) => 
                            setNotificationSettings({ ...notificationSettings, emailNotifications: checked })
                          }
                        />
                      </div>

                      <div className="flex items-center justify-between">
                        <div>
