import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Eye, Edit, X, Check, Bot, Image, Video } from "lucide-react";
import type { Post } from "@shared/schema";

interface ApprovalItemProps {
  post: Post;
  onApprove: () => void;
  onReject: () => void;
  isProcessing: boolean;
}

export default function ApprovalItem({ post, onApprove, onReject, isProcessing }: ApprovalItemProps) {
  const getStatusColor = (status: string) => {
    switch (status) {
      case "pending":
        return "bg-amber-50 text-amber-700 border-amber-200";
      case "approved":
        return "bg-green-50 text-green-700 border-green-200";
      case "rejected":
        return "bg-red-50 text-red-700 border-red-200";
      default:
        return "bg-gray-50 text-gray-700 border-gray-200";
    }
  };

  const formatTimeAgo = (date: Date | string) => {
    const now = new Date();
    const postDate = new Date(date);
    const diffInHours = Math.floor((now.getTime() - postDate.getTime()) / (1000 * 60 * 60));

    if (diffInHours < 1) return "Just now";
    if (diffInHours === 1) return "1 hour ago";
    if (diffInHours < 24) return `${diffInHours} hours ago`;
    if (diffInHours < 48) return "Yesterday";
    return `${Math.floor(diffInHours / 24)} days ago`;
  };

  const getPlatformIcon = (platform: string) => {
    const iconMap: { [key: string]: string } = {
      "Instagram": "fab fa-instagram text-pink-500",
      "Facebook": "fab fa-facebook text-blue-600",
      "X (Twitter)": "fab fa-twitter text-blue-400",
      "TikTok": "fab fa-tiktok text-gray-800",
      "LinkedIn": "fab fa-linkedin text-blue-700",
    };
    return iconMap[platform] || "fab fa-share text-gray-500";
  };

  return (
    <Card className={`border-2 ${post.status === "pending" ? "border-amber-200 bg-amber-50/30" : ""}`}>
      <CardContent className="p-6">
        <div className="flex items-start justify-between mb-4">
          <div className="flex items-center space-x-3">
            <div className="flex space-x-1">
              {post.platforms.map((platform) => (
                <i key={platform} className={getPlatformIcon(platform)} />
              ))}
            </div>
            <div>
              <p className="font-medium text-foreground">
                {post.content.slice(0, 30)}...
              </p>
              <div className="flex items-center space-x-2 text-sm text-muted-foreground">
                <span>Created {formatTimeAgo(post.createdAt!)}</span>
                {post.aiGenerated && (
                  <>
                    <span>â€¢</span>
                    <div className="flex items-center space-x-1">
                      <Bot className="w-3 h-3" />
                      <span>AI Generated</span>
                    </div>
                  </>
                )}
              </div>
            </div>
          </div>
          <Badge variant="outline" className={getStatusColor(post.status)}>
            {post.status.charAt(0).toUpperCase() + post.status.slice(1)}
          </Badge>
        </div>

        <div className="mb-4">
          <p className="text-foreground mb-2">{post.content}</p>
          <div className="flex items-center space-x-4 text-sm text-muted-foreground">
            {post.mediaUrls && post.mediaUrls.length > 0 && (
              <span className="flex items-center space-x-1">
                {post.mediaUrls[0].includes('video') ? (
                  <>
                    <Video className="w-4 h-4" />
                    <span>Video attached</span>
                  </>
                ) : (
                  <>
                    <Image className="w-4 h-4" />
                    <span>Image attached</span>
                  </>
                )}
              </span>
            )}
            {post.scheduledFor && (
              <>
                <span>â€¢</span>
                <span>
                  Scheduled for: {new Date(post.scheduledFor).toLocaleDateString('en-US', {
                    weekday: 'short',
                    hour: 'numeric',
                    minute: '2-digit',
                  })}
                </span>
              </>
            )}
          </div>
        </div>

        <div className="flex items-center justify-between pt-4 border-t border-border">
          <div className="flex items-center space-x-2">
            <Button variant="link" size="sm" className="p-0 h-auto">
              <Eye className="w-4 h-4 mr-1" />
              Preview
            </Button>
            <Button variant="link" size="sm" className="p-0 h-auto">
              <Edit className="w-4 h-4 mr-1" />
              Edit
            </Button>
          </div>

          {post.status === "pending" && (
            <div className="flex items-center space-x-3">
              <Button
                variant="outline"
                size="sm"
                onClick={onReject}
                disabled={isProcessing}
                className="text-destructive border-destructive hover:bg-destructive hover:text-destructive-foreground"
              >
                <X className="w-4 h-4 mr-1" />
                Reject
              </Button>
              <Button
                size="sm"
                onClick={onApprove}
                disabled={isProcessing}
                className="bg-green-600 hover:bg-green-700 text-white"
              >
                <Check className="w-4 h-4 mr-1" />
                Approve & Schedule
              </Button>
            </div>
          )}

          {post.status === "rejected" && post.rejectionReason && (
            <div className="text-sm text-destructive">
              Rejected: {post.rejectionReason}
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/content/content-ca.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { ChevronLeft, ChevronRight, Plus } from "lucide-react";
import type { Post } from "@shared/schema";

interface ContentCalendarProps {
  posts: Post[];
  onDateSelect?: (date: Date) => void;
  onPostClick?: (post: Post) => void;
  onCreatePost?: () => void;
}

export default function ContentCalendar({ 
  posts, 
  onDateSelect, 
  onPostClick, 
  onCreatePost 
}: ContentCalendarProps) {
  const [currentDate, setCurrentDate] = useState(new Date());

  const navigateMonth = (direction: 'prev' | 'next') => {
    const newDate = new Date(currentDate);
    if (direction === 'prev') {
      newDate.setMonth(currentDate.getMonth() - 1);
    } else {
      newDate.setMonth(currentDate.getMonth() + 1);
    }
    setCurrentDate(newDate);
  };

  const getDaysInMonth = (date: Date) => {
    const year = date.getFullYear();
    const month = date.getMonth();
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const daysInMonth = lastDay.getDate();
    const startingDayOfWeek = firstDay.getDay();

    const days = [];

    // Add empty cells for days before the first day of the month
    for (let i = 0; i < startingDayOfWeek; i++) {
      days.push(null);
    }

    // Add all days of the month
    for (let day = 1; day <= daysInMonth; day++) {
      days.push(day);
    }

    return days;
  };

  const getPostsForDay = (day: number) => {
    return posts.filter(post => {
      const postDate = new Date(post.scheduledFor || post.publishedAt!);
      return postDate.getDate() === day &&
             postDate.getMonth() === currentDate.getMonth() &&
             postDate.getFullYear() === currentDate.getFullYear();
    });
  };

  const getPlatformColor = (platform: string) => {
    const colorMap: { [key: string]: string } = {
      "Instagram": "bg-pink-500",
      "Facebook": "bg-blue-600", 
      "X (Twitter)": "bg-blue-400",
      "TikTok": "bg-gray-800",
      "LinkedIn": "bg-blue-700",
    };
    return colorMap[platform] || "bg-gray-500";
  };

  const monthNames = [
    "January", "February", "March", "April", "May", "June",
    "July", "August", "September", "October", "November", "December"
  ];

  const today = new Date();
  const isToday = (day: number) => {
    return today.getDate() === day && 
           today.getMonth() === currentDate.getMonth() && 
           today.getFullYear() === currentDate.getFullYear();
  };

  const days = getDaysInMonth(currentDate);

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle>Content Calendar</CardTitle>
          <div className="flex items-center space-x-4">
            <div className="flex items-center space-x-2">
              <Button variant="ghost" size="icon" onClick={() => navigateMonth('prev')}>
                <ChevronLeft className="w-4 h-4" />
              </Button>
              <span className="font-medium text-foreground min-w-[120px] text-center">
                {monthNames[currentDate.getMonth()]} {currentDate.getFullYear()}
              </span>
              <Button variant="ghost" size="icon" onClick={() => navigateMonth('next')}>
                <ChevronRight className="w-4 h-4" />
              </Button>
            </div>
            {onCreatePost && (
              <Button onClick={onCreatePost}>
                <Plus className="w-4 h-4 mr-2" />
                Schedule Post
              </Button>
            )}
          </div>
        </div>
      </CardHeader>

      <CardContent>
        {/* Calendar Grid */}
        <div className="grid grid-cols-7 gap-px bg-border rounded-lg overflow-hidden">
          {/* Calendar header */}
          {["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"].map((day) => (
            <div key={day} className="bg-muted p-3 text-center text-sm font-medium text-muted-foreground">
              {day}
            </div>
          ))}

          {/* Calendar days */}
          {days.map((day, index) => (
            <div
              key={index}
              className={`bg-card p-2 h-24 text-sm relative cursor-pointer hover:bg-muted/50 transition-colors ${
                day && isToday(day) ? "bg-blue-50 dark:bg-blue-950" : ""
              }`}
              onClick={() => day && onDateSelect && onDateSelect(new Date(currentDate.getFullYear(), currentDate.getMonth(), day))}
            >
              {day && (
                <>
                  <div className="flex items-center justify-between mb-1">
                    <span className={`font-medium text-xs ${
                      isToday(day) ? "text-blue-600 dark:text-blue-400" : "text-foreground"
                    }`}>
                      {day}
                    </span>
                    {isToday(day) && (
                      <Badge variant="outline" className="text-xs px-1 py-0 h-4 bg-blue-100 text-blue-600 border-blue-200">
                        Today
                      </Badge>
                    )}
                  </div>

                  <div className="space-y-1">
                    {getPostsForDay(day).slice(0, 2).map((post) => (
                      <div
                        key={post.id}
                        className="text-xs p-1 bg-primary/10 rounded border-l-2 border-primary cursor-pointer hover:bg-primary/20 transition-colors"
                        onClick={(e) => {
                          e.stopPropagation();
                          onPostClick && onPostClick(post);
                        }}
                      >
                        <div className="flex items-center space-x-1 mb-1">
                          {post.platforms.slice(0, 3).map((platform) => (
                            <div
                              key={platform}
                              className={`w-2 h-2 rounded-full ${getPlatformColor(platform)}`}
                            />
                          ))}
                          {post.scheduledFor && (
                            <span className="text-xs text-muted-foreground">
                              {new Date(post.scheduledFor).toLocaleTimeString('en-US', { 
                                hour: 'numeric', 
                                minute: '2-digit' 
                              })}
                            </span>
                          )}
                        </div>
                        <p className="line-clamp-2 text-foreground/80">
                          {post.content.slice(0, 40)}...
                        </p>
                      </div>
                    ))}

                    {getPostsForDay(day).length > 2 && (
                      <div className="text-xs text-muted-foreground text-center py-1">
                        +{getPostsForDay(day).length - 2} more
                      </div>
                    )}
                  </div>
                </>
              )}
            </div>
          ))}
        </div>

        {/* Legend */}
        <div className="mt-4 flex items-center justify-center space-x-6 text-sm">
          <div className="flex items-center space-x-2">
            <div className="w-3 h-3 bg-blue-600 rounded-full" />
            <span className="text-muted-foreground">Facebook</span>
          </div>
          <div className="flex items-center space-x-2">
            <div className="w-3 h-3 bg-pink-500 rounded-full" />
            <span className="text-muted-foreground">Instagram</span>
          </div>
          <div className="flex items-center space-x-2">
            <div className="w-3 h-3 bg-blue-400 rounded-full" />
            <span className="text-muted-foreground">X (Twitter)</span>
          </div>
          <div className="flex items-center space-x-2">
            <div className="w-3 h-3 bg-blue-700 rounded-full" />
            <span className="text-muted-foreground">LinkedIn</span>
          </div>
          <div className="flex items-center space-x-2">
            <div className="w-3 h-3 bg-gray-800 rounded-full" />
            <span className="text-muted-foreground">TikTok</span>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

